
public class Data_op {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a= 20;
		int b= 10;
		int c;
		c=a+b;
		System.out.println("Sum of a & b is "+c);
		c=a-b;
		System.out.println("Difference of a & b is "+c);
		c=a*b;
		System.out.println("Product of a & b is "+c);
		c=a/b;
		System.out.println("Quotient of a & b is "+c);
		c=++a;
		System.out.println("Increment of a is "+c);
		c=--b;
		System.out.println("Decrement of b is "+c);
		
		double x = 20.02;
		double y = 10.01;
		double z;
				
		z=x+y;
		System.out.println("Sum of x & y is "+z);
		z=x-y;
		System.out.println("Differenze of x & y is "+z);
		z=x*y;
		System.out.println("Product of x & y is "+z);
		z=x/y;
		System.out.println("Quotient of x & y is "+z);
		z=++x;
		System.out.println("Increment of x is "+z);
		z=--y;
		System.out.println("Decrement of y is "+z);
		
	
		char ch = 's';
		System.out.println("Value of character is " +ch);
		
		boolean flag = true;
		System.out.println("Value of boolean is " +flag);

		

	}

}
